// Get the meta data array for the character post
$meta_data = get_post_meta( get_the_ID(), '_custom_post_options', true );

// Check if the gallery images exist within the meta data
if ( isset( $meta_data['additional_chara_img'] ) && !empty( $meta_data['additional_chara_img'] ) ) {
    // Convert the comma-separated string into an array
    $gallery_images = explode( ',', $meta_data['additional_chara_img'] );

    echo '<div class="character-gallery">';
    
    // Loop through the gallery images
    foreach ( $gallery_images as $image_id ) {
        // Get the image URL
        $image_url = wp_get_attachment_url( $image_id );

        // Output the image with the desired structure and attributes
        echo '<img alt="Character Image" height="80" class="ml-3 mr-3 mb-2 mt-2" src="' . esc_url( $image_url ) . '" />';
    }

    echo '</div>';
} else {
    echo '<p>No additional character images found.</p>';
}
?>


